import { createContext, useContext } from 'react';
export const CheckboxOthersContext = createContext(undefined);
export const useCheckboxOthers = () => {
    const context = useContext(CheckboxOthersContext);
    if (!context) {
        throw new Error(`useCheckboxOthers must be used within a Checkbox.OthersWrapper component`);
    }
    return context;
};
